package com.charts.general.entity.coupon;import com.charts.general.entity.AbstractDataEntity;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Table;@Entity@Table(name = "data_pid")@NoArgsConstructor@Getter@Setterpublic class CouponEntity extends AbstractDataEntity {	@Column(name = "platnost")	private String validity;	@Column(name = "typ_predaja")	private String type;	@Column(name = "dospeli")	private Integer adults;	@Column(name = "studenti")	private Integer students;	@Column(name = "prenosna")	private Integer portable;	@Column(name = "dochodcovia")	private Integer seniors;	@Column(name = "junior")	private Integer junior;	@Column(name = "dieta")	private Integer children;}