package com.charts.general.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;@Entity@Table(name = "data_pid")@NoArgsConstructor@Getter@Setterpublic class CouponEntity {	@Id	@GeneratedValue(strategy = GenerationType.IDENTITY)	@Column(name = "id")	@JsonIgnore	private Long id;	@Column(name = "kod")	@JsonProperty("kod")	private String code;	@Column(name = "mesiac")	private String month;	@Column(name = "rok")	private Integer year;	@Column(name = "platnost")	private String validity;	@Column(name = "typ_predaja")	private String type;	@Column(name = "dospeli")	private Integer adults;	@Column(name = "studenti")	private Integer students;	@Column(name = "prenosna")	private Integer portable;	@Column(name = "dochodcovia")	private Integer seniors;	@Column(name = "junior")	private Integer junior;	@Column(name = "dieta")	private Integer children;	public CouponEntity(String code, String month, Integer year, String validity, String type, Integer adults, Integer students, Integer portable, Integer seniors, Integer junior, Integer children) {		this.code = code;		this.month = month;		this.year = year;		this.validity = validity;		this.type = type;		this.adults = adults;		this.students = students;		this.portable = portable;		this.seniors = seniors;		this.junior = junior;		this.children = children;	}}