package com.charts.general.entity;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Table;@Entity@Table(name = "data_pid")@NoArgsConstructor@Getter@Setterpublic class CouponEntity extends AbstractDataEntity {	@Column(name = "platnost")	private String validity;	@Column(name = "typ_predaja")	private String type;	@Column(name = "dospeli")	private Integer adults;	@Column(name = "studenti")	private Integer students;	@Column(name = "prenosna")	private Integer portable;	@Column(name = "dochodcovia")	private Integer seniors;	@Column(name = "junior")	private Integer junior;	@Column(name = "dieta")	private Integer children;	public CouponEntity(String code, String month, Integer year, String validity, String type, Integer adults, Integer students, Integer portable, Integer seniors, Integer junior, Integer children) {		super(code, month, year);		this.validity = validity;		this.type = type;		this.adults = adults;		this.students = students;		this.portable = portable;		this.seniors = seniors;		this.junior = junior;		this.children = children;	}}