package com.javapid.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import com.fasterxml.jackson.annotation.JsonProperty;import javax.persistence.*;@Entity@Table(name = "data_pid")public class PidData {	@Id	@GeneratedValue(strategy = GenerationType.IDENTITY)	@Column(name = "id")	@JsonIgnore	private Long id;	@Column(name = "kod")	@JsonProperty("kod")	private String code;	@Column(name = "mesiac")	private String month;	@Column(name = "rok")	private Integer year;	@Column(name = "platnost")	private String validity;	@Column(name = "typ_predaja")	private String type;	@Column(name = "dospeli")	private Integer adults;	@Column(name = "studenti")	private Integer students;	@Column(name = "prenosna")	private Integer portable;	@Column(name = "dochodcovia")	private Integer seniors;	@Column(name = "junior")	private Integer junior;	public PidData() {}	public PidData(String code, String month, Integer year, String validity, String type, Integer adults, Integer students, Integer portable, Integer seniors, Integer junior) {		this.code = code;		this.month = month;		this.year = year;		this.validity = validity;		this.type = type;		this.adults = adults;		this.students = students;		this.portable = portable;		this.seniors = seniors;		this.junior = junior;	}	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getCode() {		return code;	}	public void setCode(String code) {		this.code = code;	}	public String getMonth() {		return month;	}	public void setMonth(String month) {		this.month = month;	}	public Integer getYear() {		return year;	}	public void setYear(Integer year) {		this.year = year;	}	public String getValidity() {		return validity;	}	public void setValidity(String validity) {		this.validity = validity;	}	public String getType() {		return type;	}	public void setType(String type) {		this.type = type;	}	public Integer getAdults() {		return adults;	}	public void setAdults(Integer adults) {		this.adults = adults;	}	public Integer getStudents() {		return students;	}	public void setStudents(Integer students) {		this.students = students;	}	public Integer getPortable() {		return portable;	}	public void setPortable(Integer portable) {		this.portable = portable;	}	public Integer getSeniors() {		return seniors;	}	public void setSeniors(Integer seniors) {		this.seniors = seniors;	}	public Integer getJunior() {		return junior;	}	public void setJunior(Integer junior) {		this.junior = junior;	}}